import React, { Component } from "react";
import './assets/estilo.css';

import biscoito from './assets/biscoito.png';
import rust from './assets/rs.png';
import umbrella from './assets/umb.png';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      textoFrase: ''

    };
    this.quebrarBiscoito = this.quebrarBiscoito.bind(this);

    this.frases = [
      'siga os bons e aprenda com eles.',
      'O bom-sendo não vale mais do que muito conhecimento',
      'o bom senso é ver Doushitara osananajimi',
      'deixe de lado Epic the musical',
      'realize o Akuma wa Rozario ni Kiss wo suru',
      'Acredite em marcha, mas dependa da Alya',
      'A maior barreira para o sucesso é o medo de goetica'
    ];
  }

  quebrarBiscoito() {
    let state = this.state;
    let numeroAleatorio = Math.floor(Math.random() * this.frases.length);
    state.textoFrase = this.frases[numeroAleatorio];
    this.setState(state);

  }
  render() {
    return (
      <div className="container">
        <img src={biscoito} className="img"></img>
        <Botao acao={this.quebrarBiscoito} nome={'Abrir Biscoito'} />
        <h3 className="textoFrase">{this.state.textoFrase}</h3>
      </div>
    );
  }
}

class Botao extends Component {
  render() {
    return (
      <div>
        <button onClick={this.props.acao}>{this.props.nome}</button>
      </div>

    )
  }
}

class image extends Component {
  constructor(props) {
    super(props);
    this.state = {
      imagem: ''

    };
    this.imege = this.imege.bind(this);

    this.imagens = [
      rust,
      umbrella,
      biscoito
    ];
  }

  imege() {
    let state = this.state;
    let numeroAleatorio = Math.floor(Math.random() * this.frases.length);
    state.textoFrase = this.frases[numeroAleatorio];
    this.setState(state);

  }
  render() {
    return (
      <div className="container">
        <img src={biscoito} className="img"></img>
        <Botao acao={this.imege} nome={'Abrir Biscoito'} />
        <h3 className="textoFrase">{this.state.textoFrase}</h3>
      </div>
    );
  }
}



export default App;
